import React, { useState } from "react";
// Components
import ProgressBar from "./ProgressBar";
import Durations from "./Durations";
import TimerControls from "./TimerControls";
// Utilities
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import { minutesToDuration } from '../utils/duration';


const audioElement = new Audio(`${process.env.PUBLIC_URL}/alarm/submarine-dive-horn.mp3`);

function Pomodoro() {

  const initialState = {
    activeSession: false,
    timerRunning: false,
    onBreak: false,
    // Minutes
    focusSetting: 25,
    breakSetting: 5,
    // Seconds
    focusElapsed: 0,
    breakElapsed: 0,
  };

  // Set timer data
  const [timerData, setTimerData] = useState({ ...initialState });

  // Simplified object variables
  const activeSession = timerData.activeSession;
  const timerRunning = timerData.timerRunning;
  const onBreak = timerData.onBreak;
  const focusSetting = timerData.focusSetting;
  const breakSetting = timerData.breakSetting;
  const focusElapsed = timerData.focusElapsed;
  const breakElapsed = timerData.breakElapsed;

  // Change timer settings
  const handleTimerSettingChange = ({ currentTarget }) => {
    const type = currentTarget.name;
    const value = currentTarget.value;

    // Set limits for Focus and Break timers
    const validatedValue = type === "focusSetting" ? Math.min(60, Math.max(parseInt(value), 5)) : Math.min(15, Math.max(parseInt(value), 1));

    setTimerData({
      ...timerData,
      [type]: validatedValue,
    });
  }

  // Timer play/pause
  const playPause = () => {
    // Create an active session if it doesn't exist
    if (!activeSession) {
      setTimerData({
        ...timerData,
        activeSession: true,
        timerRunning: !timerRunning
      });
    } else {
      setTimerData({
        ...timerData,
        timerRunning: !timerRunning
      });
    }
  }

  // Restore timer to default state
  const terminateSession = () => {
    setTimerData({
      ...initialState
    });
  }

  // Add to elapsed time according to session type
  useInterval(
    () => {
      // Check for 100% progress
      if ((focusSetting * 60) === focusElapsed) {
        playAudio();
        setTimerData({
          ...timerData,
          onBreak: true,
          focusElapsed: 0
        });
      } else if ((breakSetting * 60) === breakElapsed) {
        playAudio();
        setTimerData({
          ...timerData,
          onBreak: false,
          breakElapsed: 0
        });
      }

      // Less than 100% progress
      else {
        if (!onBreak) {
          setTimerData({
            ...timerData,
            focusElapsed: focusElapsed + 1
          });
        } else {
          setTimerData({
            ...timerData,
            breakElapsed: breakElapsed + 1
          });
        }
      }
    },
    timerRunning ? 1000 : null
  );

  async function playAudio() {
    try {
      await audioElement.play();
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              Focus Duration: {minutesToDuration(focusSetting)}
            </span>
            <div className="input-group-append">
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                name="focusSetting"
                onClick={handleTimerSettingChange}
                value={(focusSetting - 5)}
              >
                <span className="oi oi-minus" />
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                name="focusSetting"
                onClick={handleTimerSettingChange}
                value={(focusSetting + 5)}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                Break Duration: {minutesToDuration(breakSetting)}
              </span>
              <div className="input-group-append">
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  name="breakSetting"
                  onClick={handleTimerSettingChange}
                  value={breakSetting - 1}
                >
                  <span className="oi oi-minus" />
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  name="breakSetting"
                  onClick={handleTimerSettingChange}
                  value={breakSetting + 1}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <TimerControls activeSession={activeSession} timerRunning={timerRunning} toggle={playPause} terminate={terminateSession} />

      {activeSession ? (
      <div>
        <Durations {...timerData} />
        <ProgressBar {...timerData} />
      </div>
      ) : ( null )}
    </div>
  );
}

export default Pomodoro;
