import React from "react";
import classNames from "../utils/class-names";

function TimerControls({ activeSession, timerRunning }, change) {
    // Change timer settings
    const handleTimerSettingChange = ({ currentTarget }) => {
      const type = currentTarget.name;
      const value = currentTarget.value;

      // Set limits for Focus and Break timers
      const validatedValue = type === "focusSetting" ? Math.min(60, Math.max(parseInt(value), 5)) : Math.min(15, Math.max(parseInt(value), 1));

      setTimerData({
        ...timerData,
        [type]: validatedValue,
      });
    }

    return (
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={() => toggle}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !timerRunning,
                  "oi-media-pause": timerRunning,
                })}
              />
            </button>
            {activeSession ? (
            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
              onClick={() => terminate}
            >
              <span className="oi oi-media-stop" />
            </button>
            ) : ( null )}
          </div>
        </div>
      </div>
    );
}

export default TimerControls;
