import React from "react";
import classNames from "../utils/class-names";

function TimerControls(timerData, setTimerData, initialState) {
  const activeSession = timerData.activeSession;
  const timerRunning = timerData.timerRunning;

  // Timer play/pause
  const playPause = () => {
    // Create an active session if it doesn't exist
    if (!activeSession) {
      setTimerData({
        ...initialState,
        activeSession: true,
        timerRunning: !timerRunning
      });
    } else {
      setTimerData({
        ...initialState,
        timerRunning: !timerRunning
      });
    }
  }

  // Restore timer to default state
  const terminateSession = () => {
    setTimerData({
      ...initialState
    });
  }

  return (
    <div className="row">
      <div className="col">
        <div
          className="btn-group btn-group-lg mb-2"
          role="group"
          aria-label="Timer controls"
        >
          <button
            type="button"
            className="btn btn-primary"
            data-testid="play-pause"
            title="Start or pause timer"
            onClick={playPause}
          >
            <span
              className={classNames({
                oi: true,
                "oi-media-play": !timerRunning,
                "oi-media-pause": timerRunning,
              })}
            />
          </button>
          {activeSession ? (
          <button
            type="button"
            className="btn btn-secondary"
            title="Stop the session"
            onClick={terminateSession}
          >
            <span className="oi oi-media-stop" />
          </button>
          ) : ( null )}
        </div>
      </div>
    </div>
  );
}

export default TimerControls;
